import React from "react";
import {check} from 'react-icons-kit/feather/check'
import {x} from 'react-icons-kit/feather/x'
import { ButtonToolbar } from 'reactstrap';
import { Icon } from 'react-icons-kit'
import Nav from "./layouts/Nav";
import ProfileBar from "./layouts/ProfileBar";
import { withAuth } from '@okta/okta-react';

export default withAuth(class PatientPermissions extends React.Component {
  constructor (props){
    super(props);
    this.state={
      param:null,
      patID:null,
      requests:[],
      blockdata:[]
    }
    this.componentWillMount = this.componentWillMount.bind(this);
    this.putblockdata = this.putblockdata.bind(this);
  }

  componentWillMount(){
    this.props.auth.getUser().then(res =>{
      console.log(res);
      const patID = res.preferred_username;
      console.log(patID);
      const paramdata="patID="+patID;
      this.setState({param:paramdata})   
    }).then(()=>{
    fetch('http://130.147.175.222:8099/querypatreq?'+this.state.param,{
     method: 'get'
    }).then(res =>{if (res.status>=200 && res.status<300) {
    return res.json();
    }
    else {
      console.log('sometihng went wrong');
          }
        }).then(function (json){
          console.log(json)
          for (var i in json){
          console.log(json[i].Key);
          console.log(json[i].Record);
          let req={
            reqID:json[i].Key,
            record:json[i].Record
          }          
          this.setState({
            requests:[...this.state.requests,req]
          });
          }
          console.log(this.state.requests);
        }).then(()=>{
          console.log(this.state.param);
          this.putblockdata();
        });
      });
  }

  putblockdata(){
    fetch('http://130.147.175.222:8099/chaindashboard?'+this.state.param,{
     method: 'get'
   }).then(res =>{if (res.status>=200 && res.status<300) {
     return res.json();
  }
 else {
   console.log('sometihng went wrong');
      }
    }).then(function (json){
      var count = 1;
      for (var i in json){
        let blocks={
          srno:count,
          block:json[i]
        };
        this.setState({
          blockdata:[...this.state.blockdata,blocks]
        });
        count = count +1;
      }
    });
  }

  acceptRequest(Req,PatientID) {
    console.log('The accept button was clicked. ');
    var payload = {
      "reqID": Req,
      "response": "accepted",
      "patID": PatientID
    };

     fetch('http://130.147.175.222:8099/response',{
       method: 'post',
       body : JSON.stringify(payload),
       headers: {'Content-Type':'application/json','Access-Control-Allow-Origin':'*'}
     }).then(res =>{if (res.status>=200 && res.status<300) {
       console.log(res);
     }
   else {
     console.log('sometihng went wrong');
        }
      }).catch(err => err);

    console.log(payload);
  }

  rejectRequest(Req,PatientID) {  
    console.log('The button was clicked. ');
    var payload = {
      "reqID": Req,
      "response": "rejected",
      "patID": PatientID
    };

     fetch('http://130.147.175.222:8099/response',{
       method: 'post',
       body : JSON.stringify(payload),
       headers: {'Content-Type':'application/json','Access-Control-Allow-Origin':'*'}
     }).then(res =>{if (res.status>=200 && res.status<300) {
       console.log(res);
     }
   else {
     console.log('sometihng went wrong');
        }
      }).catch(err => err);

    console.log(payload);
  }

  revokeRequest(Req,PatientID) {  
    console.log('The button was clicked. ');
    var payload = {
      "reqID": Req,
      "patID": PatientID
    };

     fetch('http://130.147.175.222:8099/revoke',{
       method: 'post',
       body : JSON.stringify(payload),
       headers: {'Content-Type':'application/json','Access-Control-Allow-Origin':'*'}
     }).then(res =>{if (res.status>=200 && res.status<300) {
       console.log(res);
     }
   else {
     console.log('sometihng went wrong');
        }
      }).catch(err => err);

    console.log(payload);
  }

  render() {
    console.log("featured");

    const TableRow1 = ({ Req, ProviderID, Category, CategoryID, Status}) => {
      return (
        <tr>
          <td>{Req}</td>
          <td>{ProviderID}</td>
          <td>{Category}</td>
          <td>{CategoryID}</td>
          <td>{Status}</td>
          <td>
          <ButtonToolbar>
          <button type="button" onClick={()=>this.acceptRequest(Req,this.state.patID)} className="btn btn-success"><Icon size={'18'} icon={check}/></button>
          <button type="button" onClick={()=>this.rejectRequest(Req,this.state.patID)} className="btn btn-danger"><Icon size={'18'} icon={x}/></button>
         </ButtonToolbar>
         </td>
       </tr>
